  const dispatch = useDispatch();
  const toogleCartHandler = () => {
    dispatch(uiAction.toogle());
  };
  return (
    <button className={classes.button} onClick={toogleCartHandler}>
      <span>My Cart</span>
      <span className={classes.badge}>1</span>
